import mongoose from 'mongoose';
import dotenv from 'dotenv';
import path from 'path';
import fs from 'fs';
import { fileURLToPath } from 'url';

// Load environment variables
dotenv.config();

// Get current directory
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Import models after configuring environment
import Product from './models/Product.js';

// Connect to MongoDB
const connectDB = async () => {
  try {
    console.log('Attempting to connect to MongoDB...');
    const conn = await mongoose.connect(process.env.MONGO_URI);
    console.log(`MongoDB Connected: ${conn.connection.host}`);
    return conn;
  } catch (error) {
    console.error(`Error: ${error.message}`);
    process.exit(1);
  }
};

// Function to create a data URL from an image file
const createDataURLFromImage = (filePath, contentType = 'image/jpeg') => {
  try {
    // Check if file exists
    if (!fs.existsSync(filePath)) {
      console.error(`File not found: ${filePath}`);
      return null;
    }
    
    // Read file as buffer
    const fileData = fs.readFileSync(filePath);
    
    // Convert to base64
    const base64Data = fileData.toString('base64');
    
    // Create data URL
    return `data:${contentType};base64,${base64Data}`;
  } catch (error) {
    console.error(`Error creating data URL: ${error}`);
    return null;
  }
};

// Repair product images by replacing with placeholders
const repairProductImages = async () => {
  try {
    console.log('Starting repair of product images...');
    const products = await Product.find();
    
    // Path to placeholder images
    const placeholderFront = path.join(__dirname, '../public/placeholder-product.jpg');
    const placeholderBack = path.join(__dirname, '../public/placeholder-product-back.jpg');
    
    // Create data URLs for placeholders
    const frontDataURL = createDataURLFromImage(placeholderFront) || 
      'data:image/jpeg;base64,';
    
    const backDataURL = createDataURLFromImage(placeholderBack) || 
      'data:image/jpeg;base64,';
    
    let fixedCount = 0;
    
    for (const product of products) {
      // Force replace all images
      if (product.images && product.images.length > 0) {
        // Create new images array with placeholders
        const newImages = [];
        
        // First image is front view
        newImages.push({
          data: frontDataURL,
          contentType: 'image/jpeg'
        });
        
        // Second image is back view
        newImages.push({
          data: backDataURL,
          contentType: 'image/jpeg'
        });
        
        // Update the product with fixed images
        product.images = newImages;
        await product.save();
        
        console.log(`Fixed images for product: ${product.name}`);
        fixedCount++;
      }
    }
    
    console.log(`Repair completed! Fixed ${fixedCount} products.`);
    return { success: true, fixedCount };
    
  } catch (error) {
    console.error('Repair error:', error);
    return { success: false, error: error.message };
  }
};

// Run the script
const main = async () => {
  try {
    await connectDB();
    const result = await repairProductImages();
    console.log('Repair result:', result);
    process.exit(0);
  } catch (error) {
    console.error('Error during repair:', error);
    process.exit(1);
  }
};

main(); 